PROGRAM MonteCarlo
  implicit none

  ! input variables
  character(100) :: lenstr
  character(100) :: trialstr
  integer :: length
  integer :: trialsize
  integer :: trialres

  if (command_argument_count() == 2) then
    call get_command_argument(1, lenstr)
    call get_command_argument(2, trialstr)

    read(lenstr, *) length
    read(trialstr, *) trialsize

    trialres = trial(length, trialsize)

    print *, trialres, trialsize, real(trialres) / real(trialsize)
  else
    print *, "Expected 2 arguments, got ", command_argument_count()
  end if

contains

